import React, { Component } from "react";
import List from "@material-ui/core/List";
import TextField from "@material-ui/core/TextField";
import ChatItem from "./ChatItem";

class ChatRoom extends Component {
  state = { chatHistory: [], input: "" };
  componentDidMount() {
    this.props.messageEvent(this.handleMessage);
  }

  handleMessage = entry => {
    this.setState({ chatHistory: this.state.chatHistory.concat(entry) });
    console.log(this.state.chatHistory);
  };

  onInputChange = event => {
    this.setState({ input: event.target.value });
  };

  onSendMessage = () => {
    if (!this.state.input) {
      return;
    }

    this.props.sendMessage(this.state.input);
    this.setState({ input: "" });
  };

  addChatMessage = entry => {
    this.setState({ chatHistory: this.state.chatHistory.concat(entry) });
  };

  renderedList() {
    let i = 0;
    return this.state.chatHistory.map(entry => {
      i++;
      return <ChatItem key={i} entry={entry} />;
    });
  }

  renderedChatBox() {
    return (
      <TextField
        id="outlined-multiline-static"
        label="Multiline"
        multiline
        rows="4"
        margin="normal"
        variant="outlined"
        onChange={this.onInputChange}
        value={this.state.input}
        onKeyPress={e =>
          e.key === "Enter" ? (e.preventDefault(), this.onSendMessage()) : null
        }
      />
    );
  }

  render() {
    return (
      <div>
        <List>{this.renderedList()}</List>
        <form>{this.renderedChatBox()}</form>
      </div>
    );
  }
}

export default ChatRoom;
