import React, { Component } from "react";
import socket from "../socket";
import ChatRoom from "./ChatRoom";

class App extends Component {
  state = { client: socket(), chatHistory: [] };

  render() {
    return (
      <div className="App">
        <h1>Hello Chat</h1>
        <ChatRoom chatHistory={this.state.chatHistory} />
        //<button onClick={this.state.client.message}>Send Message</button>
      </div>
    );
  }
}

export default App;
