import React, { Component } from "react";
import People from "@material-ui/icons/People";
import Popover from "@material-ui/core/Popover";
import Typography from "@material-ui/core/Typography";
import Collapse from "@material-ui/core/Collapse";
import { withStyles } from "@material-ui/core/styles";

const styles = theme => ({
  typography: {
    margin: "10px"
  },
  span: {
    cursor: "pointer",
    fontSize: "20px"
  },
  people: {
    position: "relative",
    top: "5px",
    fontSize: "26px"
  }
});

class UsersPopper extends Component {
  state = { anchorEl: null };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  handleClick = event => {
    this.setState({ anchorEl: event.currentTarget });
  };
  renderUsers = () => {
    const { users, classes } = this.props;
    return users.map(user => {
      return <Typography className={classes.typography}>{user}</Typography>;
    });
  };

  render() {
    const { anchorEl } = this.state;
    const { users, classes } = this.props;
    const open = Boolean(anchorEl);

    return (
      <div>
        <span className={classes.span} onClick={this.handleClick}>
          {users.length}
          <People className={classes.people} />
        </span>

        <Popover
          id="simple-popper"
          open={open}
          anchorEl={anchorEl}
          onClose={this.handleClose}
          anchorPosition={{ top: 500, left: 500 }}
          anchorOrigin={{
            vertical: "top",
            horizontal: "center"
          }}
          transformOrigin={{
            vertical: "top",
            horizontal: "left"
          }}
          TransitionComponent={Collapse}
          transitionDuration={{ enter: 2000, exit: 200 }}
        >
          <Typography className={classes.typography}>
            Users Connected:
          </Typography>
          {this.renderUsers()}
        </Popover>
      </div>
    );
  }
}

export default withStyles(styles)(UsersPopper);
