import React, { Component } from "react";
import List from "@material-ui/core/List";
import TextField from "@material-ui/core/TextField";
import { withStyles } from "@material-ui/core/styles";
import ChatItem from "./ChatItem";

const styles = theme => ({
  list: {
    width: "100%",
    maxWidth: 400,
    backgroundColor: "grey",
    position: "relative",
    overflow: "auto",
    height: "600px",
    maxHeight: 600,
    border: "2px solid"
  },
  textField: {
    width: "100%",
    maxWidth: 400
  }
});

class ChatRoom extends Component {
  state = { chatHistory: [], input: "" };
  panel = React.createRef();

  componentDidMount() {
    this.props.messageEvent(this.handleMessage);
  }

  handleMessage = entry => {
    this.setState({ chatHistory: this.state.chatHistory.concat(entry) });
    console.log(this.state.chatHistory);
  };

  onInputChange = event => {
    this.setState({ input: event.target.value });
  };

  onSendMessage = () => {
    if (!this.state.input) {
      return;
    }

    this.props.sendMessage(this.state.input);
    this.addChatMessage(this.state.input);
    this.scrollChatToBottom();
    this.setState({ input: "" });
  };

  addChatMessage = entry => {
    this.setState({
      chatHistory: this.state.chatHistory.concat({ message: entry })
    });
  };

  scrollChatToBottom = () => {
    console.log(this.panel.scrollTOp);
    this.panel.scrollBottom(this.panel.scrollHeight);
  };

  renderedList() {
    let i = 0;
    return this.state.chatHistory.map(entry => {
      i++;
      return <ChatItem key={i} entry={entry} />;
    });
  }

  renderedChatBox() {
    return (
      <TextField
        className={this.props.classes.textField}
        id="outlined-multiline-static"
        label="Multiline"
        multiline
        rows="4"
        margin="normal"
        variant="outlined"
        onChange={this.onInputChange}
        value={this.state.input}
        onKeyPress={e =>
          e.key === "Enter" ? (e.preventDefault(), this.onSendMessage()) : null
        }
      />
    );
  }

  render() {
    return (
      <div ref={this.panel}>
        <List className={this.props.classes.list}>{this.renderedList()}</List>
        <form>{this.renderedChatBox()}</form>
      </div>
    );
  }
}

export default withStyles(styles)(ChatRoom);
