import React, { Component } from "react";
import List from "@material-ui/core/List";
import TextField from "@material-ui/core/TextField";
import { withStyles } from "@material-ui/core/styles";
import ChatItem from "./ChatItem";
import NameModal from "./NameModal";
import UsersPopper from "./UsersPopper";
import faker from "faker";

const styles = theme => ({
  list: {
    width: "100%",
    maxWidth: 400,
    backgroundColor: "grey",
    position: "relative",
    overflow: "auto",
    height: "600px",
    maxHeight: 600,
    border: "2px solid"
  },
  textField: {
    width: "100%",
    maxWidth: 405
  }
});

class ChatRoom extends Component {
  state = {
    chatHistory: [],
    input: "",
    userName: "",
    users: [],
    avatar: faker.internet.avatar()
  };
  panel = React.createRef();

  componentDidMount() {
    this.props.messageEvent(this.handleMessage);
    this.props.userCount(this.handleUsers);
  }
  componentDidUpdate() {
    this.scrollChatToBottom();
  }

  handleMessage = entry => {
    this.setState({ chatHistory: this.state.chatHistory.concat(entry) });
  };

  handleUsers = entry => {
    this.setState({ users: entry });
  };

  onInputChange = event => {
    this.setState({ input: event.target.value });
  };

  onSendMessage = () => {
    if (!this.state.input) {
      return;
    }

    this.props.sendMessage({
      message: this.state.input,
      avatar: this.state.avatar
    });
    this.addChatMessage({
      username: this.state.userName,
      message: this.state.input,
      avatar: this.state.avatar
    });
    this.setState({ input: "" });
  };

  addChatMessage = entry => {
    this.setState({
      chatHistory: this.state.chatHistory.concat(entry)
    });
  };

  setUserName = userName => {
    this.setState({ userName });
  };

  scrollChatToBottom = () => {
    const panel = document.getElementById("scroll");

    panel.scrollTop = panel.scrollHeight;
  };

  renderedList() {
    let i = 0;
    return this.state.chatHistory.map(entry => {
      i++;
      return <ChatItem key={i} entry={entry} />;
    });
  }

  renderedChatBox() {
    return (
      <TextField
        className={this.props.classes.textField}
        autoComplete="off"
        label="Type a message"
        multiline
        rows="4"
        margin="normal"
        variant="outlined"
        onChange={this.onInputChange}
        value={this.state.input}
        onKeyPress={e =>
          e.key === "Enter" ? (e.preventDefault(), this.onSendMessage()) : null
        }
      />
    );
  }

  render() {
    return (
      <div
        style={{
          margin: "0px",
          marginRight: "auto",
          marginLeft: "auto",
          width: "500px"
        }}
      >
        <List id="scroll" className={this.props.classes.list}>
          {this.renderedList()}
        </List>
        <form>{this.renderedChatBox()}</form>
        <UsersPopper users={this.state.users} />
        <NameModal addUser={this.props.addUser} setUser={this.setUserName} />
      </div>
    );
  }
}

export default withStyles(styles)(ChatRoom);
