  state = { archorEl: null };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  handleClick = event => {
    this.setState({ archorEl: event.currentTarget });
  };
  renderUsers = () => {
    return <div />;
  };

  render() {
    const { archorEl } = this.state;
    const open = boolean(archorEl);
    return (
      <div>
        <span style={{ cursor: "pointer" }} onClick={this.handleClick}>
          {this.props.users.length}
